import React,{Component} from 'react';
import './Footer.scss';

class Footer extends Component {
   state = {
      phoneNumber: ''
   }

   onChange = e => {
       this.setState({[e.target.name]:e.target.value})
   }

    render(){
        const {phoneNumber} =  this.state;

        return (
            <div className="footer">
               <div className="firstContainer">
                    <h2 className="">Text yourself a link to download TikTok</h2>
                    <div className="form-wrap">
                       <form >
                           <input value="AL+355" readonly="" className="" />
                           <img src="" />
                           <input name="phone" value={phoneNumber} onChange={this.onChange} placeholder="Phone Number" />
                           <button type="submit" class="jsx-811635370">Send</button>
                       </form>
                    </div>
                    <div className="tips">
                       <p>By clicking the "send" button, you confirm that you agree to our Terms of Use 
                           and acknowledge you have read and understood our Privacy Policy
                      </p>
                    </div>
               </div>
    
               <div>
    
               </div>
            </div>
        )
    }
}

export default Footer;
